<script lang="ts">
  import { DatePicker } from '@svelte-plugins/datepicker'
  import { format } from 'date-fns'

  const today = new Date()

  export let onchange:
    | undefined
    | ((dateStart: Date | string, dateEnd: Date | string) => void) = undefined

  let startDate: Date | string = today
  let endDate: Date | string = today
  let dateFormat = 'yyyy-MM-dd'
  let isOpen = false

  let formattedStartDate = ''

  const onClearDates = () => {
    startDate = 'string'
    endDate = 'string'
  }

  const toggleDatePicker = () => (isOpen = !isOpen)
  const formatDate = (date: Date | string) =>
    (date && format(new Date(date), dateFormat)) || ''

  $: formattedStartDate = formatDate(startDate)
  $: formattedEndDate = formatDate(endDate)

  $: {
    console.log('Raw dateStart:', startDate)
    console.log('Raw endDate:', endDate)
    console.log('Formatted start date:', formattedStartDate)
    console.log('Formatted end date:', formattedEndDate)
    onchange?.(startDate, endDate)
  }
</script>

<div class="date-filter">
  <DatePicker bind:isOpen bind:startDate bind:endDate isRange showPresets>
    <div class="date-field" on:click={toggleDatePicker} class:open={isOpen}>
      <i class="icon-calendar" />
      <div class="date">
        {#if startDate}
          {formattedStartDate} - {formattedEndDate}
        {:else}
          Pick a date
        {/if}
      </div>
      {#if startDate}
        <span on:click={onClearDates}>
          <i class="os-icon-x" />
        </span>
      {/if}
    </div>
  </DatePicker>
</div>

<style>
  .date-field {
    align-items: center;
    background-color: #fff;
    border-bottom: 1px solid #e8e9ea;
    display: inline-flex;
    gap: 8px;
    min-width: 100px;
    padding: 16px;
    z-index: 9999;
  }

  .date-field.open {
    border-bottom: 1px solid #0087ff;
    z-index: 9999;
  }

  .date-field .icon-calendar {
    background: url(data:image/png;base64,)
      no-repeat center center;
    background-size: 14px 14px;
    height: 14px;
    width: 14px;
    z-index: 9999;
  }
</style>
